<!DOCTYPE html><html lang="en"><table class="table table-striped table-bordered">    <thead>    <tr>        <th>ID</th>        <th>Title</th>        <th>Status</th>        <th>Date</th>        <th>Author</th>    </tr>    </thead>    <tbody>    <?php $offset = $this->uri->segment(3, 0) + 1; ?>    <?php foreach ($query->result() as $row): ?>        <tr>            <td><a target="_blank" href="<?php echo base_url("?c=repository&m=reviewPaper&id=").$row ->paperid?>"><?php echo $row ->paperid ?></a></td>            <td><a target="_blank" href="<?php echo base_url("?c=repository&m=reviewPaper&id=").$row ->paperid?>"><?php echo $row->title ?></a></td>            <td><?php if($row->status == 1){ ?>                    Submitted                <?php } else if($row->status == 2){ ?>                    Approved                <?php } else if($row->status == 3){ ?>                    Returned                <?php } ?>            </td>            <td><?php echo $row->uploaddate; ?></td>            <td><a target="_blank" href="<?php echo base_url("?c=repository&m=reviewPaper&id=").$row ->paperid?>"><?php echo $row->name; ?></a></td>        </tr>    <?php endforeach; ?>    </tbody></table><div align="right" id="NumRecords">    <label class="label" style="color: #0c0c0c" for="collection">Total:<?php echo $total_rows?></label></div><nav class='text-center'>    <?php echo $pagination_links; ?></nav></html>